%script_trabajo_especialclearclcepsilon = 0.1;[probabilidad, todas_las_probabilidades] = calcularProbabilidad(epsilon);fprintf('La probabilidad de exito: %d\n', probabilidad);% Graficamos cómo fue evolucionando la probabilidad iteración a iteraciónfigure, plot(todas_las_probabilidades);hold onplot(todas_las_probabilidades);xlabel('Numero de iteracion');ylabel('Probabilidad');ylim([0 1]);legend('Evolucion de la probabilidad', 'Probabilidad analitica');fprintf('Las probabilidades parciales en las primeras 20');Y = std(todas_las_probabilidades (1:20))fprintf('Las probabilidades parciales en las ultimas 20');X= std(todas_las_probabilidades(end-19:end))epsilon = 0.01;[probabilidad, todas_las_probabilidades] = calcularProbabilidad(epsilon);fprintf('La probabilidad de exito: %d\n', probabilidad);% Graficamos cómo fue evolucionando la probabilidad iteración a iteraciónfigure, plot(todas_las_probabilidades);hold onplot(todas_las_probabilidades);xlabel('Numero de iteracion');ylabel('Probabilidad');ylim([0 1]);legend('Evolucion de la probabilidad', 'Probabilidad analitica');fprintf('Las probabilidades parciales en las primeras 20');Y = std(todas_las_probabilidades (1:20))fprintf('Las probabilidades parciales en las ultimas 20');X= std(todas_las_probabilidades(end-19:end))epsilon = 0.001;[probabilidad, todas_las_probabilidades] = calcularProbabilidad(epsilon);fprintf('La probabilidad de exito: %d\n', probabilidad);% Graficamos cómo fue evolucionando la probabilidad iteración a iteraciónfigure, plot(todas_las_probabilidades);hold onplot(todas_las_probabilidades);xlabel('Numero de iteracion');ylabel('Probabilidad');ylim([0 1]);legend('Evolucion de la probabilidad', 'Probabilidad analitica');fprintf('Las probabilidades parciales en las primeras 20');Y = std(todas_las_probabilidades (1:20))fprintf('Las probabilidades parciales en las ultimas 20');X= std(todas_las_probabilidades(end-19:end))